custom_summary &lt;- function(input_vector){
#ortalama vector_mean &lt;- sum(input_vector)/length(input_vector) vector_mean_hazir_fonksiyon &lt;- mean(input_vector)
#medyan if (length(input_vector)%%2==1) { vector_median &lt;- sort(input_vector)[(length(input_vector)+1)/2] } else { median_element_1_index &lt;- length(input_vector)/2 median_element_2_index &lt;- median_element_1_index+1 vector_median &lt;- (sort(input_vector)[median_element_1_index] + sort(input_vector)[median_element_2_index])/2 } vector_median_hazir_fonksiyon &lt;- median(input_vector)
#standart sapma vector_std &lt;- 0 for (element in input_vector){ vector_std &lt;- vector_std + (element - vector_mean)^2 } vector_std &lt;- vector_std / (length(input_vector)-1) vector_std &lt;- sqrt(vector_std) vector_std_hazir_fonksiyon &lt;- sd(input_vector)
#minimum ve maksimum vector_max &lt;- sort(input_vector)[length(input_vector)] vector_min &lt;- sort(input_vector)[1] vector_max_hazir_fonksiyon &lt;- max(input_vector) vector_min_hazir_fonksiyon &lt;- min(input_vector)
#istenen degiskenleri liste haline getirip return edelim sonuclar &lt;- list(ortalama = vector_mean, ortalama_hazir_fonksiyon = vector_mean_hazir_fonksiyon, medyan = vector_median, medyan_hazir_fonksiyon = vector_median_hazir_fonksiyon, standart_sapma = vector_std, standart_sapma_hazir_fonksiyon = vector_std_hazir_fonksiyon, maksimum = vector_max, maksimum_hazir_fonksiyon = vector_max_hazir_fonksiyon, minimum = vector_min, minimum_hazir_fonksiyon = vector_min_hazir_fonksiyon) return(sonuclar) }
for (i in 1:ncol(dataset)){ print(names(dataset)[i]) print(custom_summary(dataset[[i]])) }
